pragma solidity ^0.8.0;

library AddressUtils {
    function toString(address account) internal pure returns (string memory) {
        bytes32 value = bytes32(uint256(uint160(account)));
        bytes memory alphabet = '0123456789abcdef';
        bytes memory chars = new bytes(42);

        chars[0] = '0';
        chars[1] = 'x';

        for (uint256 i = 0; i < 20; i++) {
            chars[2 + i * 2] = alphabet[uint8(value[i + 12] >> 4)];
            chars[3 + i * 2] = alphabet[uint8(value[i + 12] & 0x0f)];
        }

        return string(chars);
    }

    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function sendValue(address payable account, uint256 amount) internal {
        (bool success, ) = account.call{ value: amount }('');
        require(success, 'AddressUtils: failed to send value');
    }

    function functionCall(address target, bytes memory data)
        internal
        returns (bytes memory)
    {
        return
            functionCall(target, data, 'AddressUtils: failed low-level call');
    }

    function functionCall(
        address target,
        bytes memory data,
        string memory error
    ) internal returns (bytes memory) {
        return _functionCallWithValue(target, data, 0, error);
    }

    function functionCallWithValue(
        address target,
        bytes memory data,
        uint256 value
    ) internal returns (bytes memory) {
        return
            functionCallWithValue(
                target,
                data,
                value,
                'AddressUtils: failed low-level call with value'
            );
    }

    function functionCallWithValue(
        address target,
        bytes memory data,
        uint256 value,
        string memory error
    ) internal returns (bytes memory) {
        require(
            address(this).balance >= value,
            'AddressUtils: insufficient balance for call'
        );
        return _functionCallWithValue(target, data, value, error);
    }

    function _functionCallWithValue(
        address target,
        bytes memory data,
        uint256 value,
        string memory error
    ) private returns (bytes memory) {
        require(
            isContract(target),
            'AddressUtils: function call to non-contract'
        );

        (bool success, bytes memory returnData) = target.call{ value: value }(
            data
        );

        if (success) {
            return returnData;
        } else if (returnData.length > 0) {
            assembly {
                let returnData_size := mload(returnData)
                revert(add(32, returnData), returnData_size)
            }
        } else {
            revert(error);
        }
    }
}
","keccak256":"0x427b64d138edb39914556505367376c7d5ad00ecca42db79dca8276404393e0b","license":"MIT"},"@solidstate/contracts/utils/UintUtils.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

library UintUtils {
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return '0';
        }

        uint256 temp = value;
        uint256 digits;

        while (temp != 0) {
            digits++;
            temp /= 10;
        }

        bytes memory buffer = new bytes(digits);

        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }

        return string(buffer);
    }
}
","keccak256":"0x58c2a621bfa49d032bee277f6f1803b95567cb42178e33d3c0e0a04066991b62","license":"MIT"},"contracts/nft/facets/ERC1155Config.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;
pragma experimental ABIEncoderV2;

import '@solidstate/contracts/access/OwnableInternal.sol';
import '@solidstate/contracts/token/ERC1155/metadata/ERC1155Metadata.sol';

import '../interfaces/IERC2981.sol';
import '../interfaces/IERC1155Rewardable.sol';
import '../lib/AppStorage.sol';

abstract contract ERC1155Config is
    OwnableInternal,
    IERC2981,
    IERC1155Rewardable,
    ERC1155Metadata
{
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;
    address immutable _custodian;
    AppStorage internal s;

    constructor(address c) {
        _custodian = c;
        s.status = _NOT_ENTERED;
    }

    event TokenRoyaltyBpsSet(uint16);

    modifier nonReentrant() {
        require(s.status != _ENTERED, "ReentrancyGuard: reentrant call");
        s.status = _ENTERED;
        _;
        s.status = _NOT_ENTERED;
    }

    function setURI(string memory baseURI) external onlyOwner {
        _setBaseURI(baseURI);
    }

    function setRoyaltyInfo(address recipient, uint16 bps) external onlyOwner {
        s.royalty = RoyaltyInfo(recipient, bps);
        emit TokenRoyaltyBpsSet(bps);
    }

    function royaltyInfo(uint256, uint256 value)
        external
        view
        override
        returns (address, uint256)
    {
        RoyaltyInfo memory r = s.royalty;
        return (r.recipient, (value * r.bps) / 10000);
    }

    function earningToken() external override view returns(address) {
        return s.earningToken;
    }

    function custodian() external override view returns(address) {
        return _custodian;
    }

    function finalized() external view returns(uint128) {
        return s.finalized;
    }

    function supplyOf(uint id) external view returns(uint) {
        return s.supply[id];
    }

    function supplyOfBatch(
        uint[] calldata ids
    ) external view returns(uint[] memory) {
        uint[] memory res = new uint[](ids.length);
        for (uint i = 0; i < ids.length; i++) {
            res[i] = s.supply[ids[i]];
        }
        return res;
    }

    function earning(uint tokenId) external view returns(uint) {
        uint128 start = uint128(tokenId >> 128);
        uint128 end = uint128(tokenId);
        return _earning(start, end);
    }

    function _earning(uint128 start, uint128 end)
        internal
        view
        returns(uint value)
    {
        // daily token
        if (end - start == 86400) {
            value = s.daily[end];
        // weekly token
        } else if (end - start == 604800) {
            value = s.weekly[end];
        // biweekly token
        } else if (end - start == 1209600) {
            value = s.weekly[end] + s.weekly[end - 604800];
        }
    }
}
