        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

abstract contract PausableModifier {
    modifier whenPaused() {
        require(LibPausable.layout().paused, 'Pausable: not paused');
        _;
    }

    modifier whenNotPaused() {
        require(!LibPausable.layout().paused, 'Pausable: paused');
        _;
    }
}

